mapper.update:
BEGIN
MERGE INTO COURSE C USING DUAL ON ( C.COURSE_NAME = #{item.courseName} ) WHEN NOT MATCHED THEN INSERT ( COURSE_INDEX, COURSE_NO, COURSE_NAME, START_END, TAKE_TIME, DISTANCE, CONTENT ) VALUES ( SEQ_COURSE_NO.NEXTVAL, #{item.courseNo}, #{item.courseName}, #{item.startEnd}, #{item.takeTime}, #{item.distance}, '입력된 내용이 없습니다' );
END;

UPDATE
	  COURSE
   SET
   	  CONTENT = #{content}
WHERE
	  COURSE_INDEX = #{courseIndex}
